Vue.component("dz-card", {
  render(createElement) {
    let childs = [];
    if (this.$slots.header) {
      childs.push(
        createElement("div", { class: "layui-card-header" }, [
          this.$slots.header
        ])
      );
    }
    if (this.$slots.body || this.$slots.default) {
      childs.push(
        createElement("div", { class: "layui-card-body" }, [
          this.$slots.body || this.$slots.default
        ])
      );
    }
    return createElement("div", { class: "layui-fluid" }, [
      createElement("div", { class: "layui-card" }, childs)
    ]);
  }
});
