class Request {
  constructor(request) {
    this.request = request;
  }

  get(key) {
    if (key === undefined) {
      return this.request.body;
    }
    return (
      this.request.params[key] ||
      this.request.body[key] ||
      this.request.query[key]
    );
  }

  set(key, value) {
    this.get()[key] = value;
  }

  add(key, value) {
    if (this.get(key)) return;
    this.get()[key] = value;
  }

  getFile() {
    return this.request.file;
  }
}

module.exports = Request;
